            title: 'ssadsa',
            body: '',
            date: new Date().getTime()
        }
        
        const doc = await db.collection( `${uid}/journal/notes`).add( newNote) 

        dispatch( activeNote( doc.id, newNote) );
        dispatch( addNewNote( doc.id, newNote) );
    }
}

