import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { SHSidebar } from '../../actions/ui'




export const ButtonResponsive = () => {


    const dispatch = useDispatch()
    
    const sidebar = useSelector(state => state.ui)
    const handleClickResponsive = () =>{
        if (sidebar) {
            dispatch(SHSidebar(1))
            
        } else {
            dispatch(2)
        }
    }

    return (
        <div>
            <button className="hamburger" onClick={handleClickResponsive()}>
                <svg viewBox="0 0 100 80" width="30" height="30" fill="#000">
                <rect width="90" height="10"></rect>
                <rect y="30" width="80" height="10"></rect>
                <rect y="60" width="70" height="10"></rect>
                </svg>
            </button>
        </div>
    )
}
