import React from 'react'
import { JournalEntries } from './JournalEntries'
import { useDispatch, useSelector } from 'react-redux'
import { startLogout } from '../../actions/auth'
import { startNewNote } from '../../actions/notes'



export const photoDefault = "https://res.cloudinary.com/alejo-jpe/image/upload/v1629902279/guest_c84jr7.png"

export const Sidebar = () => {

    const dispatch = useDispatch()
    const handleLogout = () => {
        dispatch( startLogout() )
    }

    const handleAddNew = () => {
        dispatch(startNewNote())
    }

    
    const state = useSelector(state => state.auth)

    const photoURL = state.photo
    const name = state.name 

    // const handleChangeProfilePhoto = (e) => {
    //     const file = e.target.files[0];
    //     if (file) {
    //         dispatch(startProfilePhoto(file, photoURL))
    //     }
    // }

    // const handlePictureClick = ()=> {
    //     document.querySelector('#fileSelector2').click()
    //     console.log("object")
    // }


    return (
        <aside className="journal__sidebar">

        <div class="respmenu">
                <input type="checkbox" />
                <i class="fas fa-bars"></i>
                <i class="fas fa-times"></i>
                <nav>
                <ul>
                    <li><a href="#" >Item 1</a></li>
                    <li><a href="#" >Item 2</a></li>
                    <li><a href="#" >Item 3</a></li>
                    <li><a href="#" >Item 4</a></li>
                    <li><a href="#" >Item 5</a></li>
                </ul>
                </nav>
            </div>
            
            <div className="journal__sidebar-navbar">

                <div className="journal__profile">
                    
                    {
                        (photoURL)
                            ?<div className="profilePhoto">
                                <img src={ ` ${photoURL } `} alt="" />
                            </div>
                            
                            :<div className="profilePhoto">

                                <img src={ ` ${photoDefault } `} alt="" />
                            </div>
                    }

                                <input 
                                    id="fileSelector2"
                                    type="file"
                                    name="file"
                                    style={{display: 'none'}}
                                />

                        <span className="journal__name mt-5 ml-5" >{name}</span>

                </div>

                <button 
                    className="journal__logout btn"
                    onClick={handleLogout}
                >
                    Logout
                </button>
            </div>
            
            <div 
                className= "journal__new-entry"
                
            >
                <i 
                    className="far fa-calendar-plus fa-5x"
                    onClick={ handleAddNew }
                >
                </i>
                <p className="mt-5">
                    New entry
                </p>
            </div>

                <JournalEntries />
                

        </aside>



    )
}
