import React from 'react'
import { JournalEntries } from './JournalEntries'
import { useDispatch, useSelector } from 'react-redux'
import { startLogout } from '../../actions/auth'
import { startNewNote, startUploading } from '../../actions/notes'




export const Sidebar = () => {

    const dispatch = useDispatch()
    const handleLogout = () => {
        dispatch( startLogout() )
    }

    const handleAddNew = () => {
        dispatch(startNewNote())
    }

    
    const state = useSelector(state => state.auth)

    const photoURL = state.photo
    const name = state.name 

    const handleChangeProfilePhoto = (e) => {
        const file = e.target.files[0];
        if (file) {
            dispatch(startUploading(file))
        }
    }

    const handlePictureClick = ()=> {
        document.querySelector('#fileSelector2').click()
    }

    const photoDefault = "https://res.cloudinary.com/alejo-jpe/image/upload/v1629902279/guest_c84jr7.png"

    return (
        <aside className="journal__sidebar">
            
            <div className="journal__sidebar-navbar">

                <div className="journal__profile">
                    
                    {
                        (photoURL)
                            ?<div className="profilePhoto">
                                <img onClick={handlePictureClick} src={ ` ${photoURL } `} alt="" />
                            </div>
                            
                            :<div className="profilePhoto">


                                <img  onClick={handlePictureClick} src={ ` ${photoDefault } `} alt="" />
                            </div>
                    }

<input 
                                    id="fileSelector2"
                                    type="file"
                                    name="file"
                                    style={{display: 'none'}}
                                    onChange={ handleChangeProfilePhoto }
                                />

                        <span className="journal__name mt-5 ml-5" >{name}</span>

                </div>

                <button 
                    className="journal__logout btn"
                    onClick={handleLogout}
                >
                    Logout
                </button>
            </div>
            
            <div 
                className= "journal__new-entry"
                
            >
                <i 
                    className="far fa-calendar-plus fa-5x"
                    onClick={ handleAddNew }
                >
                </i>
                <p className="mt-5">
                    New entry
                </p>
            </div>

                <JournalEntries />

        </aside>
    )
}
